template:
:root {
/* Here are the colors defined for the output
The background and font color can be adjusted */
/*background colour */
--background: #006;
/*Font Colour */
--fontcolor: #fff;
/* Font */
--fontart: arial;
//color: #fff;
/* Backround Colour of List: Odd lines */
--backgroundfirst: #006;
/* Font Colour of List: Odd lines */
--fontfirst: #fff;
/* Backround Colour of List: Even lines */
--backgroundsec: #309;
/* Font Colour of List: Even lines */
--fontsec: #fff;
/* Background Colour of Faults */
--backgroundfault: #ff0;
/* Font Colour of Faults */
--fontfault: #006;
}
body {

/* defines the font size to the total aspect ratio */
font-size: 1em;
background-color: var(--background);
color: var(--fontcolor);
padding:0 0 0 0;
}

/* Arrival */
.head1 {font-size: 2.5em}
/* Departure*/
.head2 {font-style: italic; font-size: 1.5em}
/* Time */
.head3 {font-size: 2.0em; text-align: left}
/* Logo */
.head4 { font-size: 2.0em; text-align: left; color: var(--fontcolor)}
.it {font-style: italic; font-size: 1.3em; text-align: left}
/* background colour of the header and footer */
td.head {background-: var(--background)}
td.foot {background-color: var(--background)}

table.head {
table-layout:fixed;
overflow: hidden;
width: 100%;
height: 50%;
font-family: var(--fontart);
border: 0px;
border-color: #fff;
<!-- border: 1px solid white; -->
border-spacing: 0;
margin:0;
padding: 0;
}
table.list {
table-layout:fixed;
overflow: hidden;
width: 100%;
height: 100%;
font-family: var(--fontart);
border: 0px;
border-spacing: 0;
margin:0;
padding: 0;
}
p { padding: 0; margin: 0;
}
table.foot {
table-layout:fixed;
overflow: hidden;
background-color: var(--background);
width: 100%;
height: 100%;
font-family: var(--fontart);
border: 0px;
border-spacing: 0;
margin:0;
padding: 0;
}

div.run {
background-color: var(--backgroundfault);
white-space: nowrap;
overflow: hidden;
}
span.fault2 {
/*display:block;*/
margin-left:100%;
font-size: 2em;
color: var(--fontfault);
padding: 0px;
/*height: 2em;*/
/*width: 100%;*/
-webkit-animation: textrun 25s linear infinite;
animation: textrun 25s linear infinite;
}
@-webkit-keyframes textrun {
0% { transform: translate(0%, 0);}
100% { margin-left:-100%;transform: translate(-100%, 0);}
}

@keyframes textrun {
0% { transform: translate(0, 0);}
100% { margin-left:-100%; transform: translate(-100%, 0); }
}
span.fault1 {
font-size: 2em;
color: var(--fontfault);
background-color: var(--backgroundfault);
padding: 0px;
/*height: 2em;*/
/*width: 100%;*/
}
th {
background-color: var(--background);
color: var(--fontcolor);
font-size: 1.3em;
text-align: left;
margin:0;
padding: 0 0 0 0;
height: 1.5em;
/*border-bottom: 0px solid #ffffff;*/
}
/* Background color default of a line */
tr {
background-color: var(--background);
color: var(--fontcolor);
/* line height */
height : 2em;
}
/* Background color of odd rows */
tr:nth-child(even) {
background-color: var(--backgroundfirst);
color: var(--fontfirst);
/* line height */
height : 2em;
}
/* Background color of even rows */
tr:nth-child(odd) {
background-color: var(--backgroundsec);
color: var(--fontsec);
/* line height */
height : 2em;
}
/* Formatting the individual cells/fields of the list */
.point {color: --fontcolor; text-align: center; position: absolut;}
.station_arrival_time {text-align: left; font-size: 1.5em}
.station_departure_time {text-align: left; font-size: 1.5em}
.train {text-align: left; font-size: 1.3em}
.first_station {text-align: left; font-size: 1.5em; height: 2em}
.last_station {text-align: left; font-size: 1.5em; height: 2em}
.via {padding-left: 10px}

div.DB-logo {
display: table-cell;
position: relative;
width: 100px;
padding-bottom: 60%; /*override this inline for aspect ratio other than square */
}
